import React, { Component } from 'react';
import { Container, Row, Col, Form, Table } from 'react-bootstrap';
import { connect } from 'react-redux';
import firebase from "firebase";
import "firebase/database";

class SectionAde extends Component {

    constructor (props) {

        super(props);

        this.state = {
            data:[]
        }


        
    }

    getFireData () {
        // firebaseにアクセス 
        let db = firebase.database();
        let ref = db.ref('t_monthly/' + this.props.userid + '/' + this.state.year + '/' + this.state.month + '/'  );
        let self = this;
        
        ref.on('value', (snapshot) => {
            if (snapshot.val() != null){

                self.setState({ data: snapshot.val() });
            
            }
        });        
    }

    render () {

        let result = [];

        for (let i=1; i<=3; i++) {

            result.push(<tr key={i}>
                <td className="sectionade-namewidth">氏名　太郎</td>
                <td>
                    <Form.Control className="sectionade-selectwidth" value="×" size="sm" as="select">
                        <option value="○" >○</option>
                        <option value="×" >×</option>
                    </Form.Control>
                </td>
            </tr>);
        }

        let str = this.props.division_day ;
        let date = str.split( '-' );
        let strDate = date[0] + "年" + date[1] + "月" + date[2] + "日";

        return (<div class="children-box">
            <Container>
                <Row className="monthly-input">
                    <Col sm={2}> {strDate} </Col>
                    <Col sm={1}>{ this.props.division }</Col>
                </Row>
                <br/>
                <Row className="monthly-input">
                    <Col sm="auto">開始時刻</Col>
                    <Col sm={2}>
                        <Form.Control size="sm" type="date" />
                    </Col>
                    <Col sm="auto">終了時刻</Col>
                    <Col sm={2}>
                        <Form.Control size="sm" type="date" />
                    </Col>
                </Row>
                <hr/>
                <Table className="sectionade-all" bordered size="sm">
                <thead className="sectionade-header">
                    <tr>
                        <th>課名</th>
                        <th>出欠</th>
                    </tr>
                </thead>
                <tbody>{result}</tbody>
            </Table>
            </Container>
        </div>);
    }
}

SectionAde = connect((state)=>state)(SectionAde);
export default SectionAde;
