import React, { Component } from 'react';
import { Modal, ListGroup, Button } from 'react-bootstrap';

class Information extends Component {

    constructor(props){
        super(props);
    
        this.state = {
            show: false,
            modalTiltle:"",
            modalBody:""
        }
        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);
    }
    handleClose(){
        this.setState({
            show:false
        });
    }
    handleShow(e){
        this.setState({
            show:true,
            modalTiltle:e.target.value,
            modalBody:e.target.value
        });
    }
    render(){
        return (
        	<div class="index-box">
            	<span class="box-title">会社からの連絡</span>
                <ListGroup variant="flush" className="modal-group">
                    <ListGroup.Item action onClick={this.handleShow} value="Cras justo odio">Cras justo odio</ListGroup.Item>
                    <ListGroup.Item action onClick={this.handleShow} value="apibus ac facilisis in">Dapibus ac facilisis in</ListGroup.Item>
                    <ListGroup.Item action onClick={this.handleShow} value="Morbi leo risus">Morbi leo risus</ListGroup.Item>
                    <ListGroup.Item action onClick={this.handleShow} value="Porta ac consectetur ac">Porta ac consectetur ac ::after</ListGroup.Item>
                </ListGroup>
                <Modal centered show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
               	        <Modal.Title>{ this.state.modalTiltle}</Modal.Title>
                	</Modal.Header>
               	    <Modal.Body>
               	        { this.state.modalBody }
               	    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                    </Modal.Footer>
           	    </Modal>
       	    </div>
        )
    }
}

export default Information;
