class Lib {
        // 加算
       static sum() {
            var result, minute, times, i,
            len = arguments.length;
   
            for (i = 0; i < len; i++) {
                
                times = arguments[i].split(':');
                minute = this.toSecond(times[0], times[1]);
              
                if (isNaN(minute)) {

                    result = 0;

                } else {

                    if (i === 0) {
                        result = minute;
                    } else {
                        result += minute;
                    }

                }
            }
            return this.toTimeFormat(result);
        }
        // 減算
       static sub() {
        var result, minute, times, i,
        len = arguments.length;

        for (i = 0; i < len; i++) {
           
            times = arguments[i].split(':');
            minute = this.toSecond(times[0], times[1]);

            if (isNaN(minute)) {

                result = 0;

            } else {

                if (i === 0) {
                    result = minute;
                } else {
                    result -= minute;
                    if (Math.sign(result) === -1) {
                        result = 0;
                    } 
                }

            }
        }
        return this.toTimeFormat(result);
    }
        // 時間を分に変換
        static toSecond(hour, minute) {
            return (Number(hour) * 60) + Number(minute);
        }
        // 分を時間（hh:mm）のフォーマットに変換
        static toTimeFormat(fullSecond) {
            var hour, minute;
            var paddingZero = function(n) {
                return (n < 10)  ? '0' + n : n;
            };
     
            hour   = Math.floor(Math.abs(fullSecond) / 60);
            minute = Math.floor(Math.abs(fullSecond) % 60);
    
            minute = paddingZero(minute);
    
            return hour + ':' + minute;
        }
        static MyVerticallyCenteredModal(props) {
            return (
              <Modal
                {...props}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
                
                
                
                
              >
                <Modal.Header closeButton>
                  <Modal.Title id="contained-modal-title-vcenter">
                    Modal heading
                  </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <h4>Centered Modal</h4>
                  <p>
                    Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
                    dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
                    consectetur ac, vestibulum at eros.
                  </p>
                </Modal.Body>
                <Modal.Footer>
                  <Button onClick={props.onHide}>Close</Button>
                </Modal.Footer>
              </Modal>
            );
          }
          
          static App() {
            const [modalShow, setModalShow] = React.useState(false);
          
            return (
              <>
                <Button variant="primary" onClick={() => setModalShow(true)}>
                  Launch vertically centered modal
                </Button>
          
                <MyVerticallyCenteredModal
                  show={modalShow}
                  onHide={() => setModalShow(false)}
                />
              </>
            );
          }
}

export default Lib;
