import { createStore, applyMiddleware } from 'redux';
import thunkMiddleware from 'redux-thunk';
import { persistStore, persistReducer } from 'redux-persist'
import storage from 'redux-persist/lib/storage'

// ステート初期値
const initial = {
    division:''
}

// レデューサー
function calcReducer (state = initial, action ) {
    switch (action.type) {
        // 選択された課名
        case 'DIVISION':

            return {
                division:action.division
            };

        // デフォルト
        default:
            return state;
    }
}

// Redux Persistの設定
const persistConfig = {
    key: 'root',
    storage,
}

// パーシストレデューサーの作成
const persistedReducer = persistReducer( persistConfig, calcReducer)

// ストア、パーシスターの作成
let store = createStore(persistedReducer)
let pstore = persistStore(store)


// initSotre関数(redux-store.jsで必要)
export function initStore(state = initial) {
    return createStore(persistedReducer, state, applyMiddleware(thunkMiddleware, pstore))
}
