class Lib {
    // 未入力時のアラート
    static enteredalert(value, contents) {

        let result = "";

        if (value == "" ) {
            result = <Alert variant="danger">{ contents}  + "は必須項目です。"</Alert>;
        } 
        return result;
        
    }

}

export default Lib;
