import React, { Component } from 'react';
import { Modal, Button } from 'react-bootstrap';

class Information extends Component {

    constructor(props){
        super(props);
    
        this.state = {
            show: false
        }
        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);
    }
    handleClose(){
        this.setState({
            show:false
        });
    }
    handleShow(){
        this.setState({
            show:true
        });
    }
    render(){
        return (
        	<div class="index-box">
            	<span class="box-title">会社からの連絡</span>
                <ListGroup variant="flush">
                    <ListGroup.Item action onClick={this.handleShow}>Cras justo odio</ListGroup.Item>
                    <ListGroup.Item action>Dapibus ac facilisis in</ListGroup.Item>
                    <ListGroup.Item action>Morbi leo risus</ListGroup.Item>
                    <ListGroup.Item action>Porta ac consectetur ac</ListGroup.Item>
                </ListGroup>
                <Modal centered show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
               	        <Modal.Title>Modal Header</Modal.Title>
                	</Modal.Header>
               	    <Modal.Body>
               	        Modal Body
               	    </Modal.Body>
           	    </Modal>
       	    </div>
        )
    }
}

export default Information;
