import React, { Component } from 'react';
import { Container, Row, Col, Form, Button, Table } from 'react-bootstrap';

class Attendance extends Component {

    constructor (props) {
        super(props);
        this.state = {
            attendance: ""
        };
        this.onChangeSelect = this.onChangeSelect.bind(this);
        this.onChangeCheckbox = this.onChangeCheckbox.bind(this);
    }

    onChangeSelect (e) {
        this.setState ({
            year: e.target.value
        });
    }

    onChangeCheckbox (e) {
        let result = "";

        if (e.target.checked = "ture") {

            result.push(<Form.Group as={Col} md = {2.5} controlId="formGridCheckbox">
                        <div class="form-inline">
                            <Form.Label as={Col} >休暇選択</Form.Label>
                                <Form.Check 
                                    type="radio"
                                    label="半休　"
                                    name="formAttendanceRadios"
                                    id="formAttendanceRadios1"
                                    />
                                    <Form.Check
                                        type="radio"
                                        label="時間休"
                                        name="formAttendanceRadios"
                                        id="formAttendanceRadios2"
                                    />
                        </div>
                        </Form.Group>);

        } else {
            result.push();
        }

        this.setState ({
            attendance: result
        });
    }

    render () {
        let result = [];
        result.push();
        return (<div class="children-box">
            <Container>
                <Row className="monthly-input">
                    <Col md="auto">申請種別</Col>
                    <Col md="auto">
                        <Form.Control as="select" onChange={ this.onChangeSelect }>
                            <option>電車遅延</option>
                            <option>遅刻</option>
                        </Form.Control>
                    </Col>
                </Row>
                <hr/>
                <Form>
                    <Form.Row>
                        <Form.Group as={Col} md = {5} controlId="formGridState" >
                            <Form.Label>件名</Form.Label>
                            <Form.Control type="text" placeholder="" className="setting-form" />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group as={Col} md = {2} controlId="formGridState">
                            <Form.Label>出社時間</Form.Label>
                            <Form.Control type="time" placeholder="" className="setting-form" />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group as={Col} md = {5} controlId="formGridState" >
                            <Form.Label>路線名</Form.Label>
                            <Form.Control type="text" placeholder="" className="setting-form" />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group as={Col} md = {2} controlId="formGridCheckbox">
                            <Form.Check type="checkbox" label="有給消化"  onChange={ this.onChangeCheckbox }/>
                        </Form.Group>
                        { this.state.attendance }
                    </Form.Row>
                </Form>
            </Container>
            <a href="" class="btn btn--orange btn--circle btn--circle-a btn--shadow">保存</a>
        </div>);
    }
}

export default Attendance;
