import React, { Component } from 'react';
import { Container, Row, Col, Form, Button, Table } from 'react-bootstrap';


class Monthly extends Component {

    constructor (props) {
        super(props);
        this.state = {
            year:'',
            month:'',
        };
        this.onChangeYear = this.onChangeYear.bind(this);
    }

    onChangeYear (e) {
        this.setState ({
            year: e.target.value
        });
    }

    onChangeYear (e) {
        this.setState ({
            year: e.target.value
        });
    }

    render () {
        let result = [];
        let date = new Date( 2021, 4, 0);
        let lastDay = date.getDate();

        for (let i=1; i<=lastDay; i++) {
            result.push(<tr key={i}>
                <td>{[i]}</td>
                <td><Form.Control type="text" placeholder="" /></td>
                <td><Form.Control type="text" placeholder="" /></td>
                <td><Form.Control type="text" placeholder="" /></td>
            </tr>);
        }
    
        return (<div class="children-box">
            <Container>
                <Row className="monthly-input">
                    <Col md="auto">対象年月</Col>
                    <Col md="auto">
                        <Form.Control as="select" defaultValue="" onChange={this.onChangeYear}>
                            <option>2021</option>
                            <option>2022</option>
                        </Form.Control>
                    </Col>
                    <Col md="auto">年</Col>
                    <Col md="auto">
                        <Form.Control as="select" defaultValue="" onChange={this.onChangeMonth}>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                            <option value="6">6</option>
                            <option value="7">7</option>
                            <option value="8">8</option>
                            <option value="9">9</option>
                            <option value="10">10</option>
                            <option value="11">11</option>
                            <option value="12">12</option>
                        </Form.Control>
                    </Col>
                    <Col md="auto">月</Col>
                    <Col md={{ span: "auto", offset: 2 }}>月間稼働時間</Col>
                    <Col md="auto">{}</Col>
                    <Col md="auto">
                        <Button variant="secondary">初期値</Button>{' '}
                    </Col>
                </Row>
            </Container>
            <br/>
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Username</th>
                    </tr>
                </thead>
                <tbody>{result}</tbody>
            </Table>
        </div>);
    }
}

export default Monthly;
