import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Container, Row, Col, Form} from 'react-bootstrap';

class Section extends Component {

    constructor (props) {

        super(props);

        let today = new Date();
        let year = today.getFullYear();
        let day = today.getMonth() + 1;

        this.state = {
            year: year,
            month: day,
        };

        this.onChangeYear = this.onChangeYear.bind(this);
        this.onChangeMonth = this.onChangeMonth.bind(this);
        this.onClickDivision = this.onClickDivision.bind(this);

    }

    onChangeYear (e) {
        this.setState ({
            year: e.target.value
        });
    }

    onChangeMonth (e) {
        this.setState ({
            month: e.target.value
        });
    }

    onClickDivision (e) {
        return this.props.dispatch({ type: 'DIVISION', division: e.target.text});
    }

    targetSave () {

        let year = this.state.year;
        let month = this.state.month;
        let userid = this.props.userid;

        // firebaseにアクセス 
        let db = firebase.database();
        let ref = db.ref( 'm_user/'  + year + '/' + month );

        let byId = function( id ) { return document.getElementById( id ).value };

        ref.set({
             
            mail_address   : byId("mailAddress"),    // メールアドレス
            start_time     : byId("startTime"),      // 始業時間
            closing_time   : byId("closingTime"),    // 終業時間
            break_start    : byId("breakStart"),     // 休憩開始
            break_end      : byId("breakEnd"),       // 休憩終了
            break_time     : byId("breakTime"),      // 休憩時間
            working_style  : byId("workingStyle"),   // 作業形態
            contract_terms : byId("contractTerms"),  // 契約条件
            reference_time : byId("referenceTime"),  // 基準時間
            remarks1       : byId("remarks1"),       // 備考１
            remarks2       : byId("remarks2"),       // 備考２
            remarks3       : byId("remarks3"),       // 備考３
            remarks4       : byId("remarks4"),       // 備考４
            remarks5       : byId("remarks5"),       // 備考５
            route1         : byId("route1"),         // 路線１
            route2         : byId("route2"),         // 路線２
            route3         : byId("route3"),         // 路線３
            mail_cc1       : byId("mailCC1"),        // 勤怠CC1
            mail_cc2       : byId("mailCC2"),        // 勤怠CC2 

        });
    }


    render () {

        let today = new Date();
        let yearAgo = today.getFullYear()-1;
        let yearThis = today.getFullYear();
        let yearLater = today.getFullYear()+1;

        return (<div class="children-box">
            <Container>
                <Row className="monthly-input">
                    <Col md="auto">対象年月</Col>
                    <Col md="auto">
                        <Form.Control as="select" defaultValue={ yearThis } onChange={ this.onChangeYear }>
                            <option>{ yearAgo }</option>
                            <option>{ yearThis }</option>
                            <option>{ yearLater }</option>
                        </Form.Control>
                    </Col>
                    <Col md="auto">年</Col>
                    <Col md="auto">
                        <Form.Control as="select" defaultValue={ this.state.month } onChange={ this.onChangeMonth }>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                            <option value="6">6</option>
                            <option value="7">7</option>
                            <option value="8">8</option>
                            <option value="9">9</option>
                            <option value="10">10</option>
                            <option value="11">11</option>
                            <option value="12">12</option>
                        </Form.Control>
                    </Col>
                    <Col md="auto">月</Col>
                </Row>
                <hr/>
                <Form>
                    <Form.Row className="monthly-input">
                        <Col sm={1}>
                            <a href="/sectionade" onClick={ this.onClickDivision } >DA1</a>
                        </Col>
                        <Col sm={1}>開始日</Col>    
                        <Col sm={2}>
                            <Form.Control size="sm" type="date" defaultValue=""/>
                        </Col>    
                    </Form.Row>
                    <br/>
                    <Form.Row className="monthly-input">
                        <Col sm={1}>
                        <a href="/sectionade" onClick={ this.onClickDivision } >DA2</a>
                        </Col>
                        <Col sm={1}>開始日</Col>    
                        <Col sm={2}>
                            <Form.Control size="sm" type="date" defaultValue=""　/>
                        </Col>    
                    </Form.Row>
                    <br/>
                    <Form.Row className="monthly-input">
                        <Col sm={1}>
                        <a href="/sectionade" onClick={ this.onClickDivision } >DB1</a>
                        </Col>
                        <Col sm={1}>開始日</Col>    
                        <Col sm={2}>
                            <Form.Control size="sm" type="date" defaultValue=""　/>
                        </Col>    
                    </Form.Row>
                    <br/>
                    <Form.Row className="monthly-input">
                        <Col sm={1}>
                        <a href="/sectionade" onClick={ this.onClickDivision } >DB2</a>
                        </Col>
