import React, { Component } from 'react';
import { Container, Row, Col, Form, Button, Table } from 'react-bootstrap';

class Attendance extends Component {

    constructor (props) {
        super(props);
        this.state = {
        };
        this.onChangeSelect = this.onChangeSelect.bind(this);
        this.onChangeCheck = this.onChangeCheck.bind(this);
    }

    onChangeSelect (e) {
        this.setState ({
            year: e.target.value
        });
    }

    onChangeCheck (e) {
        if (e.target.checked) {
            document.getElementById("atd").className = "form-inline";
        }else{
            document.getElementById("atd").className = "form-inline d-none";
        }
    }

    render () {
        
        return (<div class="children-box">
            <Container>
                <Row className="monthly-input">
                    <Col md="auto">申請種別</Col>
                    <Col md="auto">
                        <Form.Control as="select" onChange={ this.onChangeSelect }>
                            <option>電車遅延</option>
                            <option>遅刻</option>
                        </Form.Control>
                    </Col>
                </Row>
                <hr/>
                <Form>
                    <Form.Row>
                        <Form.Group as={Col} md = {5} controlId="formGridState" >
                            <Form.Label>件名</Form.Label>
                            <Form.Control type="text" placeholder="" className="setting-form" />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group as={Col} md = {2} controlId="formGridState">
                            <Form.Label>出社時間</Form.Label>
                            <Form.Control type="time" placeholder="" className="setting-form" />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group as={Col} md = {5} controlId="formGridState" >
                            <Form.Label>路線名</Form.Label>
                            <Form.Control type="text" placeholder="" className="setting-form" />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group as={Col} md = {2} controlId="formGridCheckbox">
                            <Form.Check type="checkbox" label="有給消化" onChange={ this.onChangeCheck }/>
                        </Form.Group>
                        <Form.Group as={Col} md = {2.5} controlId="formGridCheckbox" >
                            <div class="form-inline d-none" id="atd" >
                                <Form.Label as={Col} >休暇選択</Form.Label>
                                <Form.Check 
                                    type="radio"
                                    label="半休　"
                                    name="formAttendanceRadios"
                                    id="formAttendanceRadios1"
                                />
                                <Form.Check
                                    type="radio"
                                    label="時間休"
                                    name="formAttendanceRadios"
                                    id="formAttendanceRadios2"
                                />
                            </div>
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                    <table border="1">
                            <tr>
                            <td colspan="3" rowspan="2">
                                <Button variant="outline-secondary" size="sm">本文作成</Button>
                            </td>
                            <td>CC1</td>
                            </tr>
                            <tr>
                            <td>CC2</td>
                            </tr>
                    </table>
                    </Form.Row>
                </Form>
            </Container>
            <a href="" class="btn btn--orange btn--circle btn--circle-a btn--shadow">送信</a>
        </div>);
    }
}

export default Attendance;
