import React, { Component } from 'react';
import { Container, Row, Col, Form, Table } from 'react-bootstrap';
import { connect } from 'react-redux';
import firebase from "firebase";
import "firebase/database";

class SectionAde extends Component {

    constructor (props) {

        super(props);

        this.state = {
            data:[]
        }

        this.getFireData();

        this.onClickCommit = this.onClickCommit.bind(this);
        
    }

    getFireData () {
        // firebaseにアクセス 
        let db = firebase.database();
        let ref_m = db.ref('m_section/' + this.props.division  + '/' );
        let ref_t = db.ref('t_section_attendance/' + this.props.division + '/' + this.props.division_day + '/' );
        let self = this;
        
        ref_m.on('value', (snapshot) => {
            if (snapshot.val() !== null){

                self.setState({ data: snapshot.val() });
            
            }
        });        
    }

    onClickCommit () {
        // idでhtmlからtableの要素を取得し、trタグのNodeListを取得
        let table = document.getElementById('traffic-table');
        let tbl_tr = table.querySelectorAll('tr');

        // テーブルの1行(trタグ)毎に処理
        tbl_tr.forEach(function (tr) {
            // tdタグのNodeListを取得
            let cells = tr.querySelectorAll('td');
            // firebaseの更新先設定 
            let number = cells[0].firstElementChild.value;

            if ( number === "" ) { return };

            let rowNumber = ("00" + tr.rowIndex).slice(-2);
            // firebaseにアクセス 
            let db = firebase.database();
            let ref = db.ref('t_section_attendance/' + this.props.division + '/' + this.props.division_day + '/' );

            ref.set({
                day                   : number,                            // 日
                purpose               : cells[1].firstElementChild.value,　// 目的
                vehicle               : cells[2].firstElementChild.value,  // 乗り物
                departure_and_arrival : cells[3].firstElementChild.value,  // 発～(経由)～着
                one_way               : cells[4].firstElementChild.value,  // 片往
                amount_of_money       : cells[5].firstElementChild.value   // 金額
            });
        });
    }

    render () {

        let result = [];

        for (let key in this.state.data) {

            result.push(<tr key={key} className="sectionade-tr" >
                <td>
                    <Form.Text className="sectionade-keywidth">{key}</Form.Text>
                </td>
                <td>
                    <Form.Text className="sectionade-namewidth">{ this.state.data[key] }</Form.Text>
                </td>
                <td>
                    <Form.Control className="sectionade-selectwidth" value="×" size="sm" as="select" >
                        <option value="○" >○</option>
                        <option value="×" >×</option>
                    </Form.Control>
                </td>
            </tr>);
        }

        let str = this.props.division_day ;
        let date = str.split( '-' );
        let strDate = date[0] + "年" + date[1] + "月" + date[2] + "日";

        return (<div class="children-box">
            <Container>
                <Row className="monthly-input">
                    <Col sm={2}> {strDate} </Col>
                    <Col sm={1}>{ this.props.division }</Col>
                </Row>
                <br/>
                <Row className="monthly-input">
                    <Col sm="auto">開始時刻</Col>
                    <Col sm={2}>
                        <Form.Control size="sm" type="time" />
                    </Col>
                    <Col sm="auto">終了時刻</Col>
                    <Col sm={2}>
                        <Form.Control size="sm" type="time" />
                    </Col>
                </Row>
                <hr/>
                <Table className="sectionade-all" bordered size="sm">
                <thead className="sectionade-header">
                    <tr>
                        <th>社員番号</th>
                        <th>課員名</th>
                        <th>出欠</th>
                    </tr>
                </thead>
                <tbody id="traffic-table" >{result}</tbody>
            </Table>
            </Container>
            <a href="javascript:void(0)" class="btn btn--orange btn--circle btn--circle-a btn--shadow" onClick={this.onClickCommit} >保存</a>
        </div>);
    }
}

SectionAde = connect((state)=>state)(SectionAde);
export default SectionAde;
