import React, { Component } from 'react';
import { Container, Row, Col, Form, Table } from 'react-bootstrap';
import { connect } from 'react-redux';

class SectionAde extends Component {

    constructor (props) {

        super(props);

        let today = new Date();
        let year = today.getFullYear();
        let day = today.getMonth() + 1;

        this.state = {
            year: year,
            month: day,
        };

        this.onChangeYear = this.onChangeYear.bind(this);
        this.onChangeMonth = this.onChangeMonth.bind(this);
        this.onClickDivision = this.onClickDivision.bind(this);

    }

    render () {

        let result = [];

        for (let i=1; i<=3; i++) {

            result.push(<tr key={i}>
                <td className="sectionade-namewidth">氏名　太郎</td>
                <td>
                    <Form.Control className="sectionade-selectwidth" value="×" size="sm" as="select">
                        <option value="○" >○</option>
                        <option value="×" >×</option>
                    </Form.Control>
                </td>
            </tr>);
        }


        return (<div class="children-box">
            <Container>
                <Row className="monthly-input">
                    <Col sm={2}>XXXX年XX月XX日</Col>
                    <Col sm={1}>課名</Col>
                </Row>
                <br/>
                <Row className="monthly-input">
                    <Col sm="auto">開始時刻</Col>
                    <Col sm={2}>
                        <Form.Control size="sm" type="date" />
                    </Col>
                    <Col sm="auto">終了時刻</Col>
                    <Col sm={2}>
                        <Form.Control size="sm" type="date" />
                    </Col>
                </Row>
                <hr/>
                <Table className="sectionade-all" bordered size="sm">
                <thead className="sectionade-header">
                    <tr>
                        <th>課名</th>
                        <th>出欠</th>
                    </tr>
                </thead>
                <tbody>{result}</tbody>
            </Table>
            </Container>
        </div>);
    }
}

SectionAde = connect((state)=>state)(SectionAde);
export default SectionAde;
