class Replace {
    // 日付置換
    static datereplace(value) {
    
        let date = value.split(/\-/);

            var year,month,day;
            year = date[1];
            month = date[2];
            day = date[3];
            month = month ? month.replace(/^0/,'') : '';
            day = day ? day.replace(/^0/,'') : '';
            
            return year + "年"+ month + "月" + day + "日";
    }
}

export default Replace;
