import React, { Component } from 'react';
import { Modal, ListGroup, Button, Pagination } from 'react-bootstrap';

class Information extends Component {

    constructor(props){
        super(props);
    
        this.state = {
            show: false,
            modalTiltle:"",
            modalBody:""
        }
        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);
        this.handleActive = this.handleActive.bind(this);
    }
    handleClose(){
        this.setState({
            show:false
        });
    }
    handleShow(e){
        this.setState({
            show:true,
            modalTiltle:e.target.value,
            modalBody:e.target.value
        });
    }
    render(){

        let active = 1;
        let items = [];
        let subject = [ "タイトル１", "タイトル２", "タイトル３", "タイトル４", "タイトル５", "タイトル６" ];
        let listgroup = [];

        for (let number = 1; number <= (Math.ceil(subject.length / 5)); number++) {
          items.push(
            <Pagination.Item key={number} active={number === active} onClick={this.handleActive} >
              {number}
            </Pagination.Item>,
          );
        }

        const paginationBasic = (
            <div>
                <Pagination>{items}</Pagination>
            </div>
        );

        for (let number = 0; number < subject.length; number++) {
            listgroup.push(
                <ListGroup.Item action onClick={this.handleShow} value={ subject[number] }>{ subject[number] }</ListGroup.Item>
            );
        }

        const listBasic = (
            <div>
                <ListGroup variant="flush" className="modal-group">
                    {listgroup}
                    <br/>
                    {paginationBasic}
                </ListGroup>
            </div>


        );

        return (
        	<div class="index-box">
            	<span class="box-title">会社からの連絡</span>
                {listBasic}
                <Modal centered show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
               	        <Modal.Title>{ this.state.modalTiltle}</Modal.Title>
                	</Modal.Header>
               	    <Modal.Body>
               	        { this.state.modalBody }
               	    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                    </Modal.Footer>
           	    </Modal>
       	    </div>
        )
    }
}

export default Information;
