import React, { Component } from 'react';
import { Modal, Button, Pagination } from 'react-bootstrap';
import { connect } from 'react-redux';

import firebase from "firebase";
import "firebase/storage";

class Information extends Component {

    constructor(props){
        super(props);

        let db = firebase.database();
        let ref = db.ref('t_information/');

        ref.on('value', (snapshot) => {
                self.setState({
                    data:snapshot.val()
                });
            });
    
        this.state = {
            show: false,
            modalTiltle:"",
            modalBody:"",
            active:1,
            data:[]
        }
        this.handleClose = this.handleClose.bind(this);
        this.handleShow = this.handleShow.bind(this);
        this.handleActive = this.handleActive.bind(this);
        this.getFireData = this.getFireData.bind(this);
    }
    handleClose() {
        this.setState({
            show:false
        });
    }
    handleShow(e) {
        this.setState({
            show:true,
            modalTiltle:e.target.textContent,
            modalBody:e.target.textContent
        });
    }
    handleActive(e) {
        this.setState({
            active:Number(e.target.text)
        });
    }
    getFireData() {


    }


    render(){

        let items = [];
        let listgroup = [];

        this.getFireData();

        for (let number = 1; number <= (Math.ceil(this.state.data.length / 5)); number++) {
          items.push(
            <Pagination.Item key={number} active={number === this.state.active} onClick={this.handleActive} >
              {number}
            </Pagination.Item>,
          );
        }

        const paginationBasic = (
            <div class="pagination-items">
                <Pagination>{items}</Pagination>
            </div>
        );

        for (let number = (this.state.active -1)*5; number <= (this.state.active*5 -1); number++) {

            if ( this.state.data[number] !== undefined ){
                listgroup.push(
                    <li className="list-color" onClick={this.handleShow}>
                        { this.state.data[number].message }
                    </li>
                );    
            } else {
                listgroup.push(
                    <li>-</li>
                );
            }
        }

        const listBasic = (
            <div>
                <div class="listgroup-color">
                    <ul className="modal-group">
                        {listgroup}
                    </ul>
                </div>
                <div className="list-items">
                    {paginationBasic}
                </div>
            </div>
        );

        return (
        	<div class="index-box">
            	<span class="box-title">会社からの連絡</span>
                {listBasic}
                <Modal centered show={this.state.show} onHide={this.handleClose}>
                    <Modal.Header closeButton>
               	        <Modal.Title>{ this.state.modalTiltle}</Modal.Title>
                	</Modal.Header>
               	    <Modal.Body>
               	        { this.state.modalBody }
               	    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                            Close
                        </Button>
                    </Modal.Footer>
           	    </Modal>
       	    </div>
        )
    }
}

Information = connect((state)=>state)(Information);
export default Information;
