import { createStore, applyMiddleware } from 'redux';
import thunkMiddleware from 'redux-thunk';
import { persistReducer } from 'redux-persist'
import storage from 'redux-persist/lib/storage'
import firebase from 'firebase';


// Firebaseの初期化
var config = {
    apiKey: "AIzaSyAXqs5f06pIB3WFXUeZwF9z2Ue-cxPp2iw",
    authDomain: "next-app-32b81.firebaseapp.com",
    databaseURL: "https://next-app-32b81-default-rtdb.firebaseio.com",
    projectId: "next-app-32b81",
    storageBucket: "next-app-32b81.appspot.com",
    messagingSenderId: "808565003407",
    appId: "1:808565003407:web:996357a827feec72e50cfa",
    measurementId: "G-NRZ14EXJND"
}

// Firebase初期化
var fireapp;
try {
    firebase.initializeApp(config);
} catch (error) {
    console.log(error.message);
}
export default fireapp;

// ステート初期値
const initial = {
    division:'',
    login: false,
    username: 'ログイン',
    email: ''
}

// レデューサー
function fireReducer (state = initial, action ) {
    switch (action.type) {
        // 選択された課名
        case 'DIVISION':

            return {
                division:action.division
            };

        case 'UPDATE_USER':
            return action.value;

        // デフォルト
        default:
            return state;
    }
}

// Redux Persistの設定
const persistConfig = {
    key: 'root',
    storage,
}

// パーシストレデューサーの作成
const persistedReducer = persistReducer( persistConfig, fireReducer)


// initSotre関数(redux-store.jsで必要)
export function initStore(state = initial) {
    return createStore(persistedReducer, state, applyMiddleware(thunkMiddleware))
}
