import React, { Component } from 'react';
import { Container,  Row, Col, Form } from 'react-bootstrap';


class Monthly extends Component {

    render () {
        let result = [];
        let date = new Date( 2021, 4, 0);
        let lastDay = date.getDate();

        for (let i=1; i<=lastDay; i++) {
            result.push(<tr key={i}>
                <th>{[i]}</th>
                <td>{[i]}</td>
            </tr>);
        }
    
        return (<div class="children-box">
            <Container>
                <Row>
                    <Col md="auto">対象年月</Col>
                    <Col md="auto">
                        <Form.Control as="select" defaultValue="">
                            <option>2021</option>
                            <option>2022</option>
                        </Form.Control>
                    </Col>
                    <Col md="auto">年</Col>
                    <Col md="auto">
                        <Form.Control as="select" defaultValue="">
                            <option>1</option>
                            <option>2</option>
                        </Form.Control>
                    </Col>
                    <Col md="auto">月</Col>
                    <Col md="auto">月間稼働時間</Col>
                    <Col md="auto">{}</Col>
                    <Col md="auto">月間稼働時間</Col>

                </Row>
            </Container>
            <br/>
            <table>
                <tbody>{result}</tbody>
            </table>
        </div>);
    }
}

export default Monthly;
