import React, { Component } from 'react';
import { Container, Row, Col, Form, Button, Table } from 'react-bootstrap';


class Monthly extends Component {

    constructor (props) {
        super(props);
        let today = new Date();
        let year = today.getFullYear();
        let day = today.getMonth() + 1;
        this.state = {
            year: year,
            month: day,
            attendanceTime: 0,
            leacingTime: 0,
            breakTime: 0,
        };
        this.onChangeYear = this.onChangeYear.bind(this);
        this.onChangeMonth = this.onChangeMonth.bind(this);
        this.onChangeAttendance = this.onChangeAttendance.bind(this);
        this.onChangeLeaving = this.onChangeLeaving.bind(this);
        this.onChangeBreak = this.onChangeBreak.bind(this);

    }

    onChangeYear (e) {
        this.setState ({
            year: e.target.value
        });
    }

    onChangeMonth (e) {
        this.setState ({
            month: e.target.value
        });
    }

    onChangeAttendance (e) {
        let from_time = 0;
        if (e.target.value !== null) {
            from_time = e.target.value;
        }
        let adt_time = this.state.attendanceTime;
        let [from_hour, from_minute] = from_time.split(':');
        let [adt_hour, adt_minute] = adt_time.split(':');
        let attendance_sum = from_hour + adt_hour + (from_minute + adt_minute) / 60.0;

        this.setState ({
            attendanceTime: attendance_sum
        });
    }

    onChangeLeaving (e) {
        this.setState ({
            leacingTime: e.target.value + this.state.leacingTime
        });
    }

    onChangeBreak (e) {
        this.setState ({
            breakTime: e.target.value + this.state.breakTime
        });
    }


    render () {

        let today = new Date();
        let yearAgo = today.getFullYear()-1;
        let yearThis = today.getFullYear();
        let yearLater = today.getFullYear()+1;

        let result = [];
        let date = new Date( this.state.year, this.state.month, 0);
        let lastDay = date.getDate();

        for (let i=1; i<=lastDay; i++) {

            let dayOfWeekJP = new Date( this.state.year, this.state.month -1, i);
            let dayOfWeekStrJP = [ "日", "月", "火", "水", "木", "金", "土" ];
            let dayOfWeek = dayOfWeekStrJP[ dayOfWeekJP.getDay() ];
            
            let holidayColor =""
            if ( dayOfWeek == "日" || dayOfWeek == "土") {
                holidayColor = "" + "holiday-color" + ""
            }

            result.push(<tr className={ holidayColor } key={i}>
                <td>
                    <div className="table-day">{[i]}</div>
                </td>
                <td>
                    <div className="table-day">{dayOfWeek}</div>
                </td>
                <td><Form.Control className="table-from" type="time" onChange={ this.onChangeAttendance } placeholder=""/>{this.state.attendanceTime}</td>
                <td><Form.Control className="table-from" type="time" onChange={ this.onChangeLeaving } placeholder=""/></td>
                <td><Form.Control className="table-from" type="time" onChange={ this.onChangeBreak } placeholder=""/></td>
                <td><Form.Control className="table-from" type="time" placeholder=""/></td>
                <td>
                    <Form.Control className="table-from" as="select" defaultValue="">
                        <option>Choose...</option>
                        <option>...</option>
                    </Form.Control>
                </td>
                <td><Form.Control className="table-text" type="text" placeholder="" /></td>
            </tr>);
        }

        return (<div class="children-box">
            <Container>
                <Row className="monthly-input">
                    <Col md="auto">対象年月</Col>
                    <Col md="auto">
                        <Form.Control as="select" defaultValue={ yearThis } onChange={ this.onChangeYear }>
                            <option>{ yearAgo }</option>
                            <option>{ yearThis }</option>
                            <option>{ yearLater }</option>
                        </Form.Control>
                    </Col>
                    <Col md="auto">年</Col>
                    <Col md="auto">
                        <Form.Control as="select" defaultValue={ this.state.month } onChange={ this.onChangeMonth }>
                            <option value="1">1</option>
                            <option value="2">2</option>
                            <option value="3">3</option>
                            <option value="4">4</option>
                            <option value="5">5</option>
                            <option value="6">6</option>
                            <option value="7">7</option>
                            <option value="8">8</option>
                            <option value="9">9</option>
                            <option value="10">10</option>
                            <option value="11">11</option>
                            <option value="12">12</option>
                        </Form.Control>
                    </Col>
                    <Col md="auto">月</Col>
                    <Col md={{ span: "auto", offset: 2 }}>月間稼働時間</Col>
                    <Col md="auto">{ (this.state.leacingTime - 0) - (this.state.attendanceTime - 0) - (this.state.breakTime - 0) }</Col>
                    <Col md="auto">
                        <Button variant="secondary">初期値</Button>{' '}
                    </Col>
                </Row>
            </Container>
            <br/>
            <Table className="table-all" bordered size="sm">
                <thead className="table-header">
                    <tr>
                        <th>日</th>
                        <th>曜</th>
                        <th>出社</th>
                        <th>退社</th>
                        <th>休憩(深夜含)</th>
                        <th>内 深夜休</th>
                        <th>勤怠</th>
                        <th>備考</th>
                    </tr>
                </thead>
                <tbody>{result}</tbody>
            </Table>
            <a href="" class="btn btn--orange btn--circle btn--circle-a btn--shadow">保存</a>
        </div>);
    }
}

export default Monthly;
