class Replace {
    // 日付置換
    static datereplace(value) {
    
        let date = value;
        
        date = date.replace(/^(\d+)-\(\d+)-\(\d+)$/, function(){
            var month,day;
            month = arguments[2];
            day = arguments[3];
            month = month ? month.replace(/^0/,'') : '';
            day = day ? day.replace(/^0/,'') : '';
            return arguments[1] + "年"+ month + "月" + day + "日";
        });        
    }
}

export default Replace;
