export default <style>{`
    body {

    }

   /*ヘッダーのタイトル*/
    .container-title {
        margin:0px 400px;
    }
    .title-size {
        font-size:18pt;
    }
    .top-title {
        color:#4ABDAC;
        font-weight: bold;
        text-shadow: 1px 1px 2px silver;
    }
    /*ヘッダーの個人情報とログアウト*/
    .top-nav {
        font-size:14pt;
        align-items: baseline;
        margin:5px;
    }
    /*ヘッダーのナビゲーションバー*/
    .div-nav {
        background-color: #F78733;
    }
    .base-nav {
        margin:0px 400px 100px 400px;
        font-size:14pt;
    }
    .div-nav .nav-link:hover {
        background-color: #F89042;
        cursor: pointer;
    }
    .nav-tabs .nav-link.active {
        background-color: #F16C09;
    }
    /*トップ画面*/
    .index-box {
        position: relative;
        padding: 0.5em 1em;
        border-top: solid 3px #4ABDAC;
    }
    .index-box p {
        margin:10px 400px;
        padding: 0;
    }
    .index-box .box-title {
        position: absolute;
        margin:0px 400px;
        display: inline-block;
        top: -27px;
        left: -3px;
        padding: 0 9px;
        height: 25px;
        line-height: 25px;
        font-size: 17px;
        background: #4ABDAC;
        color: #ffffff;
        font-weight: bold;
        border-radius: 5px 5px 0 0;
    }
    /*その他画面*/
    .children-box {
        font-size: 16px;
        padding: 1.5em 2em;
        margin:100px 400px;
        background: #e4fcff;/*背景色*/
        border-top: solid 6px #4ABDAC;
        box-shadow: 0 3px 4px rgba(0, 0, 0, 0.32);/*影*/
    }
    .fixed_btn
    {
      position: fixed;
      bottom: 100px; 
      right: 500px;
      padding: 6px 40px;
    }
    /*保存ボタン*/
    a.btn--red {
      background-color: #d20010;
      border: 2px solid #9f000c;
    }
    a.btn-c {
        bottom: 100px; 
        right: 500px;
        padding: 6px 40px;
      font-size: 1.8rem;
      position: relative;
      padding: 2rem 4rem 2rem 2rem;
      color: #fff;
      border-radius: 100vh;
      -webkit-box-shadow: 0 10px 10px rgba(0, 0, 0, .2);
      box-shadow: 0 10px 10px rgba(0, 0, 0, .2);
    }
    a.btn-c:before {
        position: fixed;

        font-family: 'Font Awesome 5 Free';
      font-size: 1.6rem;
      line-height: 1;
      top: calc(50% - .8rem);
      right: 1rem;
      margin: 0;
      padding: 0;
      content: '\f30b';
    }




  footer {
      color:#99c;
      font-size:12pt;
      text-align:right;
      border-bottom:1px solid #99c;
      margin:100px 400px;
      position: relative;
      bottom: 10px;
      right: 10px;
      left: 10px;
  }
  p {
      margin:0px;
      color:#669;
      font-size:12pt;
  }
  hr {
      margin:25px 0px;
  }
  tr {
      margin:0px;
  }
  th {
      font-size:14pt;
      font-weight:plain;
      text-align:left;
      padding:0px 20px;
      margin:0px;
      border-botton:1px solid gray;
  }
`} </style>
